CREATE DATABASE IF NOT EXISTS geekshubstravel CHARACTER SET Latin1 COLLATE latin1_spanish_ci;
USE geekshubstravel;
CREATE TABLE user (id INT NOT NULL AUTO_INCREMENT,username VARCHAR(45) NOT NULL,email VARCHAR(45) NOT NULL,password VARCHAR(80) NOT NULL,hash VARCHAR(80), isAdmin INT(1) NOT NULL default 0, active INT(1) NOT NULL DEFAULT 0, PRIMARY KEY (id));
CREATE TABLE travel (id INT AUTO_INCREMENT NOT NULL, city VARCHAR(45) NOT NULL, price FLOAT(8) NOT NULL, image VARCHAR(45) NOT NULL, type VARCHAR(20) NOT NULL, description VARCHAR(80) NOT NULL, active INT(1) NOT NULL DEFAULT 1, PRIMARY KEY(id));
